import React, { Component} from 'react';
import styles from './PhoneBook.module.css'

class PhoneBook extends Component {
  state = {
    name: '',
    number: ''
  }

  turnChanceName = (e) => {
    // console.log(e);
    const {name, value} = e.target
    // console.log({name, value});
    this.setState({
      [name]: value
    })
    console.log(this.state.name);
  }

  turnChanceNumber = (e) => {
  // console.log(e);
  const {number, value} = e.target
  // console.log({name, value});
  this.setState({
    [number]: value
  })
  console.log(this.state.number);
  }



  render(){
    return (
      <div className={styles.mainPhoneBook}>
        <h2 className={styles.phoneBookTitle}>Name</h2>
        <input 
        className={styles.phoneBookInput}
        type="text"
        name="name"
        value={this.state.name}
        onChange={this.turnChanceName}
        ></input>
        <h2 className={styles.phoneBookTitle}>Number</h2>
        <input 
        className={styles.phoneBookInput}
        type="text"
        name="number"
        value={this.state.number}
        onChange={this.turnChanceNumber}
        ></input>
        <button className={styles.phoneBookButton}>Add contact</button>
      </div>
  );
}
};

export default PhoneBook;
