import React, { Component} from 'react';
import styles from './PhoneBook.module.css'
import propTypes from 'prop-types'
import Contacts from '../Contacts/Contacts'

class PhoneBook extends Component {
  state = {
    name: '',
    number: ''
  }

  turnChanceName = (e) => {
    // console.log(e);
    const {name, value} = e.target
    // console.log({name, value});
    this.setState({
      [name]: value
    })
    console.log(this.state.name);
    console.log(this.state.number);
  }

  turnAdd = (e) => {
    e.preventDefault()
    const singleTusk = {
      name: this.state.name,
      number: this.state.number
    }
  }

  render(){
    return (
      <>
      <div className={styles.mainPhoneBook}>
        <form 
        onSubmit={this.turnAdd}>
        <h2 className={styles.phoneBookTitle}>Name</h2>
        <input 
          className={styles.phoneBookInput}
          type="text"
          name="name"
          value={this.state.name}
          onChange={this.turnChanceName}
        ></input>
        <h2 className={styles.phoneBookTitle}>Number</h2>
        <input 
          className={styles.phoneBookInput}
          type="text"
          name="number"
          value={this.state.number}
          onChange={this.turnChanceName}
        ></input>
        <button 
          type="submit"
          className={styles.phoneBookButton}
          onSubmit={this.turnAdd}
          >Add contact</button>
        </form>
      </div>
        <Contacts />
      </>

  );
}
};

export default PhoneBook;
