import React, { Component} from 'react';
import styles from './PhoneBook.module.css'
import propTypes from 'prop-types'
import { v4 as uuidv4 } from 'uuid';

class PhoneBook extends Component {
  initialState ={

  }

  state = {
  }

  handleName = (e) => {
    const {name, value} = e.target
    this.setState({
      [name]: value
    })
  }

  turnAdd = (e) => {
    e.preventDefault()
    this.props.addContact({...this.state})
    this.setState({...initialState})
  }

  render(){
    return (
      <>
      <div className={styles.mainPhoneBook}>
        <form 
        onSubmit={this.turnAdd}>
        <h2 className={styles.phoneBookTitle}>Name</h2>
        <input 
          className={styles.phoneBookInput}
          type="text"
          name="name"
          value={this.state.name}
          onChange={this.handleName}
        ></input>
        <h2 className={styles.phoneBookTitle}>Number</h2>
        <input 
          className={styles.phoneBookInput}
          type="text"
          name="number"
          value={this.state.number}
          onChange={this.handleName}
        ></input>
        <button 
          type="submit"
          className={styles.phoneBookButton}
          >Add contact</button>
        </form>
      </div>
      </>

  );
}
};

export default PhoneBook;
